import React from 'react';
import Card from 'react-bootstrap/Card';
import './style.css';
import Button from 'react-bootstrap/Button'

class Activity extends  React.Component {

    constructor(props) {
        super(props);
    }


    onClickAdd = (item) => {
        this.props.callFilteredListAddToPlannedActivities(item);
    }

    render() {
        return (
            <Card style={{ maxWidth: '18rem' }}>
                <Card.Img className="img-wrapper" variant="top" src={this.props.activity.image} />
                <Card.Body>
                    <Card.Title>{this.props.activity.name}</Card.Title>
                    <div className="d-flex flex-column justify-content-around align-content-center">
                        <p>Style: {this.props.activity.style}</p>
                        <p>Color: {this.props.activity.color}</p>
                        <p>Price: ${this.props.activity.price}</p>
                    <div className="d-flex justify-content-sm-center align-content-end">
                        <Button className="cartButton btn-wd" variant="outline-primary"
                                onClick={() => this.onClickAdd(this.props.activity)} block>Add</Button>
                    </div>
                    </div>
                </Card.Body>
            </Card>
        )
    }

}

export default Activity;
