import React from 'react';
import DropdownButton from "react-bootstrap/DropdownButton";
import Dropdown from "react-bootstrap/Dropdown";

import FilteredSortedClothing from './FilteredSortedClothing';
import AggregateDisplay from './AggregateDisplay'
import Navbar from "react-bootstrap/Navbar";
import {Nav, NavDropdown} from "react-bootstrap";

class BrowsingSection extends  React.Component {
    constructor(props) {
        super(props);
        this.state = {
          curr_color:"All",
          curr_style: "All",
          curr_sort: "None",

        };

        this.comparators = {
            low_high: function (a, b) {return a.price - b.price},
            high_low: function (a, b) {return b.price - a.price},
            no_sort: function (a, b) {return 0},

        }
    };

    chooseStyle = style => {
        this.setState({
            curr_style: style
        })
    };

    chooseColor = color => {
        this.setState({
            curr_color: color
        })
    };

   chooseSort = sort => {
        this.setState({
            curr_sort: sort
        })
    };


   compareStyle = item => {
        if(this.state.curr_style === "All") {
            return true
        }
        else return this.state.curr_style === item.style;
    };

   compareColor = item => {
        // all items should be shown when no filter is selected
        if(this.state.curr_color === "All") {
            return true
        }
        //otherwise check if filter === current item's corresponding property
        else return this.state.curr_color === item.color;
    };



    filterAndSortClothing() {
        let list = this.props.list
        list = list.filter(this.compareStyle);
        list = list.filter(this.compareColor)
        let comparator
        if(this.state.curr_sort === 'High To Low'){
            comparator = this.comparators.high_low
        }
        else if(this.state.curr_sort === 'Low To High'){
            comparator = this.comparators.low_high
        }
        else {
            comparator = this.comparators.no_sort
        }

        return list.sort(comparator)
    }



    render() {
        return (

                <div>



                    <div className="row sticky-top">
                        <div className="col">
                            <Navbar bg="light" expand="lg">

                                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                                <Navbar.Collapse id="basic-navbar-nav">
                                    <Nav className="mr-auto">
                                        <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                                            <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                                            <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                                            <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                                            <NavDropdown.Divider />
                                            <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                                        </NavDropdown>

                                        <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                                            <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                                            <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                                            <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                                            <NavDropdown.Divider />
                                            <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                                        </NavDropdown>

                                        <DropdownButton title="Dropdown" id="basic-nav-dropdown">
                                            <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                                            <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                                            <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                                            <NavDropdown.Divider />
                                            <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                                        </DropdownButton>
                                    </Nav>

                                </Navbar.Collapse>
                            </Navbar>
                        </div>
                        <div className="col">
                            <div className="d-flex flex-column filter-box">
                                <h5 className="text-center">Filter</h5>


                                {/*Filter Dropdown for how much energy the activity requires */}
                                <div className="d-inline-flex flex-column justify-content-start">
                                    <div className="text-left filter-header">Color:</div>
                                    <DropdownButton className="dropdown" id="dropdown-basic-button"
                                                    title={this.state.curr_color} onSelect={this.chooseColor}>
                                        <Dropdown.Item eventKey='All'>All</Dropdown.Item>
                                        <Dropdown.Item eventKey='Blue'>Blue</Dropdown.Item>
                                        <Dropdown.Item eventKey='Black'>Black</Dropdown.Item>
                                        <Dropdown.Item eventKey='White'>White</Dropdown.Item>
                                    </DropdownButton>
                                </div>

                                <div className="d-inline-flex flex-column justify-content-start">
                                    <div className="text-left filter-header">Style</div>
                                    <DropdownButton id="dropdown-basic-button" title={this.state.curr_style} onSelect={this.chooseStyle}>
                                        <Dropdown.Item eventKey='All'>All</Dropdown.Item>
                                        <Dropdown.Item eventKey='Tops'>Tops</Dropdown.Item>
                                        <Dropdown.Item eventKey='Pants'>Pants</Dropdown.Item>
                                        <Dropdown.Item eventKey='Dresses'>Dresses</Dropdown.Item>
                                        <Dropdown.Item eventKey='Shoes'>Shoes</Dropdown.Item>
                                    </DropdownButton>
                                </div>
                            </div>

                            <div className="d-flex flex-column sort-box">
                                <h5 className="text-center">Sort</h5>
                                <p className="text-left">By Price:</p>
                                <DropdownButton id="dropdown-basic-button" title={this.state.curr_sort} onSelect={this.chooseSort}>
                                    <Dropdown.Item eventKey='None'>None</Dropdown.Item>
                                    <Dropdown.Item eventKey='High To Low'>High To Low</Dropdown.Item>
                                    <Dropdown.Item eventKey='Low To High'>Low To High</Dropdown.Item>
                                </DropdownButton>
                            </div>
                        </div>



                    </div>


                    <FilteredSortedClothing
                        list = {this.filterAndSortClothing()}
                        addToCart ={this.props.addToCart}>
                    </FilteredSortedClothing>
                </div>

        )
    }
}

export default BrowsingSection;
